# counting N averages:
N=5
def countNAve(List,N):
    AveN = []
    for i in range(len(List)//N):
        summation = 0
        for j in range(N):
            summation+=List[i*N+j]
        AveN.append(summation/N)
    return AveN
v1=countNAve(v1,N)
runtime=countNAve(runtime,N)


